/** * Sequence do campo 'id' da tabela smartphone. */create sequence if not exists smartphone_pk_sequenceincrement by 1minvalue 1;/** * Tabela smartphone. */create table smartphone (  id serial primary key,  code varchar(8) unique not null check (length(code) = 8),  model varchar(255) not null,  price decimal(19,2) not null,  brand varchar(255) not null,  photo_url varchar(255) not null,  start_date timestamp not null check (start_date >= '2018-12-25'),  end_date timestamp not null check (end_date > start_date),  color varchar(5) not null check (color in ('BLACK', 'WHITE', 'GOLD', 'PINK'))) ;